        }
    }

    return (
        <AuthFormBlock>
            <h3>로그인</h3>
                <StyledInput 
                    autoComplete="id"
                    name="id"
                    placeholder="아이디"
                    value={id}
                    onChange={onChangeId}
                />
                <StyledInput 
                    autoComplete="new-password" 
                    name="password" 
                    placeholder="비밀번호" 
                    type="password"
                    value={password}
                    onChange={onChangePassword}
                />
                <Button full onClick={onClickLogin}>로그인</Button>
                <Footer>
                    <CustomLink to="/register">회원가입</CustomLink>
            </Footer>
        </AuthFormBlock>
    )
};

export default LoginForm;
